// CurrencyCollection.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import WatchList from './WatchList';
import AddCurrency from './AddCurrency';
import Navbar from './Navbar';
import Footer from './Footer';

export default function CurrencyCollection() {
  const [currency, setCurrency] = useState([]);
  const [yourWatchlist, setYourWatchlist] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  function fetchData() {
    const fetchurl = 'http://localhost:3000/total_market_cap';

    fetch(fetchurl)
      .then((response) => response.json())
      .then((data) => setCurrency(data))
      .catch((error) => console.error(error));
  }

  const addToYourList = (coin) => {
    if (!yourWatchlist.find((c) => c.id === coin.id)) {
      setYourWatchlist([...yourWatchlist, coin]);
    }
    console.log(coin);
  };

  const releaseFromList = (coin) => {
    setYourWatchlist(yourWatchlist.filter((c) => c.id !== coin.id));
  };

  const dischargeCoin = (coin) => {
    fetch(`http://localhost:3000/total_market_cap/${coin.id}`, {
      method: 'DELETE',
    })
      .then(() => {
        const updatedCoin = yourWatchlist.filter((c) => c.id !== coin.id);
        setYourWatchlist(updatedCoin);
      })
      .catch((error) => console.error(error));
  };

  const addCurrency = (newCurrency) => {
    setCurrency([...currency, newCurrency]);
  };

  return (
    <Router>
      <div>
        <Navbar />
        {/* Home Route */}
        <Route
          exact
          path="/"
          render={() => (
            <div className="container row mx-auto">
              {currency.map((coin) => (
                <div className="col-md-3" key={coin.id}>
                  <div className="cards my-4">
                    <div className="col coininfo p-4">
                      <div>
                        <h5>{coin.name}</h5>
                        <p>Market Cap</p>
                        <p>{coin.marketCap}</p>
                        <button onClick={() => addToYourList(coin)}>Watchlist</button>
                        <button onClick={() => dischargeCoin(coin)} className="my-2">
                          Delete
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        />

        {/* Watchlist Route */}
        <Route
          path="/watchlist"
          render={() => (
            <WatchList
              list={yourWatchlist}
              releaseFromYourWatchlist={releaseFromList}
              dischargeCoin={dischargeCoin}
            />
          )}
        />

        {/* Add Currency Route */}
        <Route path="/add" render={() => <AddCurrency onAddCurrency={addCurrency} />} />

        <Footer />
      </div>
    </Router>
  );
}
