import React, { useState, useEffect } from 'react';

export default function AddCurrency({ onAddCurrency }) {
  const [newCurrency, setNewCurrency] = useState({
    name: '',
    symbol: '',
    marketCap: '',
  });

  useEffect(() => {
    // Add a check to avoid making unnecessary requests
    if (Object.values(newCurrency).every((value) => value !== '')) {
      // Assuming your API endpoint is '/currencies'
      fetch('http://localhost:3000/currencies', {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newCurrency),
      })
        .then((response) => response.json())
        .then((data) => {
          onAddCurrency(data); // Assuming the server responds with the newly added currency
        })
        .catch((error) => console.error('Error adding currency:', error));
    }
  }, [newCurrency, onAddCurrency]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewCurrency({
      ...newCurrency,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // The useEffect will handle adding the currency to the database
    // No need to reset the form fields here
  };

  return (
    <div>
      <h2>Add a New Currency</h2>
      <form onSubmit={handleSubmit}>
        <label>
          <input
            type="text"
            name="name"
            value={newCurrency.name}
            onChange={handleInputChange}
            placeholder="Currency Name"
          />
        </label>
        <label>
          <input
            type="text"
            name="symbol"
            value={newCurrency.symbol}
            onChange={handleInputChange}
            placeholder="Symbol"
          />
        </label>
        <label>
          <input
            type="number"
            name="marketCap"
            value={newCurrency.marketCap}
            onChange={handleInputChange}
            placeholder="Market Cap"
          />
        </label>
        <button type="submit" className='add'>Add Currency</button>
      </form>
    </div>
  );
}
